import React from "react"
import { connect } from 'react-redux'

import CollectionsItem from '../../components/collection-item/collection-item.component'

import { selectCollections } from '../../redux/shop/shop.selectors'

import "./collection.styles.scss";

const CollectionPage = ({ collection }) => {

    const { title, items } = collection

    return (
        <div className='collection-page'>
            <h2 className='title'>{ title }</h2>
            <div className='items'>
                {
                    items.map(item => (
                        <CollectionsItem key={item.id} item={item}/>
                ))}
            </div>
        </div>
    )
}
// ownProps returns all props that we are getting on our CollectionPage component
const mapStateToProps = (state, ownProps) =>({
    collection: selectCollections(ownProps.match.params.collection)(state)
})

export default connect(mapStateToProps)(CollectionPage)
