import { createSelector} from 'reselect'

const COLLECTION_ID_MAP = {
    hats: 1,
    sneakers: 2,
    jackets: 3,
    womens: 4,
    mens: 5
}

const selectShop = state => state.shop

export const selectCollections = createSelector(
    [selectShop],
    shop => shop.collections
)
// map through the collection COLLECTION_ID_MAP by selecting it, and passing into our new select collection selector the Url
// paramters which is a string and will return the createSelector which is a called currying function that returns another function
export const selectCollection = collectionUrlParam => 
    createSelector(
        [selectCollections],
        collections => collections.find(collection => collection.id === COLLECTION_ID_MAP[collectionUrlParam])
    )
